function pagination(){$("#load_more").click(function(){var a=$("#load_size").val(),o=$("#load_val").val(),n=$("#load_type").val(),i=$("#load_id").val(),e=window.location.origin;$("#load").remove(),$.ajax({type:"POST",url:e+"/controllers/forum/pagination.php",async:!1,data:{page_number:o,type:n,size:a,id:i},success:function(a){$("#content_box").append(a)}})})}$(document).bind("ready ajaxComplete",function(){pagination()});