function menu(n){$(n).find("div.category").click(function(){var e=$(this).attr("id");$(n).hide(),menu=e+"cat",$("#"+menu).show()})}function submenu(n){$(n).find("div.category").click(function(){var e=$(this).attr("id");"TOK"!=e&&$(n).hide(),pre=n.replace("cat",""),menu=pre+e,$(menu).show()})}function back(n,e){$(n).hide(),$(e).show()}$(document).ready(function(){menu("#menu1"),submenu("#APcat"),submenu("#IBcat"),submenu("#SATcat")});